class ManhuaguiCrypto {
  static test() {
    final encryptString =
        'window["eval"](function(p,a,c,k,e,d){debugger;e=function(c){return(c<a?"":e(parseInt(c/a)))+((c=c%a)>35?String.fromCharCode(c+29):c.toString(36))};if(!\'\'.replace(/^/,String)){while(c--)d[e(c)]=k[c]||e(c);k=[function(e){return d[e]}];e=function(){return\'\w+\'};c=1;};while(c--)if(k[c])p=p.replace(new RegExp(\'\b\'+e(c)+\'\b\',\'g\'),k[c]);return p;}(\'n.j({"k":4,"l":"gï¼Œh","i":"4.2","p":q,"r":"5","m":["o.2.0","6-8.2.0","9.2.0","7.2.0","a.2.0","e.2.0","f-c.3.0","d.3.0","E.3.0","G.2.0"],"H":F,"D":I,"K":"/L/b/J/5/","C":1,"v":"","w":u,"s":t,"A":{"B":"z-x"}}).y();\',48,48,\'\'[\'splic\'](\'|\'),0,{}))';

    var imagePathList = decrypt(encryptString);
